<?phpclass AiUnitMemoryViewer{	public static function view($connectionList)	{		$html = '';				$html .= "\n<script type='text/javascript'>\n";		$html .= "<!--\n";				$html .= "function initMemoryFromDb() {";		foreach ($connectionList as $connection)		{			if ($connection['verb'] == 'aliasof')			{				$html .= "talkingRouter.conceptNameMapper.alias('".$connection['subject']."','".$connection['complement']."');\n";			}			else			{				$html .= 'talkingRouter.tautologyManager.learnStatement("'.$connection['subject'].' '.$connection['verb'].' '.$connection['complement'].'");';			}		}		$html .= "}\n";				$html .= "-->\n";		$html .= "</script>\n";				return $html;	}}?>